import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Read = () => {
    const [data, setData] = useState([]);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(true); // ✅ Loading state

    async function getData() {
        try {
            const response = await fetch("http://localhost:5001");
            const result = await response.json();

            if (!response.ok) {
                setError(result.error);
                toast.error(result.error);
            } else {
                setData(result);
            }
        } catch (err) {
            toast.error("Failed to fetch data!");
            setError("Failed to fetch data!");
        } finally {
            setLoading(false); // ✅ Hide loading when done
        }
    }

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Are you sure you want to delete this user?");
        if (!confirmDelete) return; // ✅ Prevent accidental deletion

        try {
            const response = await fetch(`http://localhost:5001/${id}`, {
                method: "DELETE"
            });

            const result = await response.json();

            if (!response.ok) {
                toast.error(result.error);
                setError(result.error);
            } else {
                toast.success("User deleted successfully!");
                setData(data.filter(user => user._id !== id)); // ✅ Optimistic UI update
            }
        } catch (err) {
            toast.error("Error deleting user!");
        }
    };

    useEffect(() => {
        getData();
    }, []);

    return (
        <div className="container my-4">
            {error && <div className="alert alert-danger">{error}</div>}
            <h2 className="text-center mb-4">All Users</h2>

            {/* ✅ Show a Loading Spinner */}
            {loading && <div className="text-center"><div className="spinner-border text-primary"></div></div>}

            <div className="row">
                {data.length > 0 ? (
                    data.map((ele) => (
                        <div key={ele._id} className="col-md-4 mb-4">
                            <div className="card shadow-lg rounded">
                                <div className="card-body">
                                    <h5 className="card-title">{ele.name}</h5>
                                    <h6 className="card-subtitle mb-2 text-muted">{ele.email}</h6>
                                    <p className="card-text">Age: {ele.age}</p>
                                    <div className="d-flex justify-content-between">
                                        <Link to={`/${ele._id}`} className="btn btn-sm btn-warning">
                                            Edit
                                        </Link>
                                        <button
                                            className="btn btn-sm btn-danger"
                                            onClick={() => handleDelete(ele._id)}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    !loading && <p className="text-center">No users found.</p>
                )}
            </div>
        </div>
    );
};

export default Read;
